require("isomorphic-fetch");
import BPromise from "bluebird";

function getJson(url) {
  return fetch(url).then(function (resp) {
    console.log(url, resp);
    return resp.json();
  });
}

function getCamp(name){
    return fetch(`http://35.190.190.219/api/${name}`).then(function(resp){
        return resp.json();
    })
}


function handleError(error) {
  console.warn(error);
  return null;
}


module.exports = {
  createAccount: function (user_info) {
    return getJson(user_info).catch(handleError);
  },
  getInfo: function (campground) {
    return getCamp(campground).catch(handleError);
  },
};
