import Layout from "../components/MyLayout.js";
import Link from 'next/link'


const indexLink = {
  display: "inline",
  textAlign: "center",
  fontSize: "1em",
  textColor: "#000000"

}

export default function Index() {
  return (
    <div class= "container" style={{background: "linear-gradient(#00bf8f, #001510)"}}>
    <Layout>
      <br />
      
      <img
        src="/static/norway.jpg"
        style={{
          marginBottom: "100px",
          padding: "25px",
          display: "block",
          width: "45%",
          margin: "auto",
        }}
      />
      <h1>Let's Go Exploring!</h1>

      <h3>Get Information On All Of Your Favorite Campgrounds</h3>
      <Link href="/search">
        <p>Go to <a style={indexLink}>Find A Campground</a> to begin your next adventure!</p>
      </Link>

    
      <style jsx>{`
        h1,
        h2,
        h3,
        h4,
        a,
        p {
          color: #1f618d;
          font-family: "Arial";
        }
    
        .button-style {
          margin: auto auto;
          cursor: pointer;
          background-color: #228b22;
          color: #ffffff;
          width: 100px;
          font-family: "Arial";
        }

        .text-style {
          margin: auto auto;
          width: 200px;
        }

        input {
          margin: auto auto;
          width: 200px;
        }

        .description {
          font-family: "Arial";
          font-size: "10px";
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
        div {
          paddingLeft: 100px;
          display: inline;
          color: green;
        }
      `}</style>
    </Layout>
    </div>
  );
}
