import Layout from "../components/MyLayout.js";

import { getInfo } from "../lib/utils";

class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = { search: "" };
    //this.handleSearch();
  }
  handleUpdate(evt) {
    this.setState({ search: evt.target.value });
  }

  async handleSearch(evt) {
    const campground = await getInfo(evt.target.value);
    console.log(campground);
    this.setState({ campground });
  }

  render() {
    const that = this;
    return (
      <Layout
        style={{ margin: "auto auto", width: "600px", textAlign: "center"}}
      >
        <h2>Campground Search</h2>
        <input
          type="text"
          className="text-style"
          value={this.state.search}
          onChange={this.handleUpdate.bind(this)}
        />
        <br />
        <br />
        <div onClick={this.handleSearch.bind(that)} className="button-style">
          Submit
        </div>
        <br /> <br />
        {this.state.campground ? (
          <div>
            <br />
            <h3>{this.state.campground.name}</h3>
            <br />
            <img
              style={{ maxWidth: "700px", maxHeight: "500px" }}
              src={this.state.campground.image_url}
            />
            <br /> <h4>{this.state.campground.closest_town}</h4>
            <br />
            <div className="description">
              <p>{this.state.campground.description}</p>
              <br />{" "}
            </div>
          </div>
        ) : null}
        {"campground" in this.state && this.state.campground == undefined ? (
          <p>{this.state.search} Camptround Not Found</p>
        ) : null}
        <style jsx>{`
          h1,
          h2,
          h3,
          h4,
          a,
          p {
            font-family: "Arial";
            color: #006600;
          }

          h2 {
            margin: auto;
            width: 20%;
            border: 3px solid green;
            top-padding: 10px;
            text-align: center;
          }

          .button-style {
            margin: auto auto;
            cursor: pointer;
            background-color: #228b22;
            color: #ffffff;
            width: 100px;
            font-family: "Arial";
          }

          .text-style {
            margin: auto;
            width: 10%;
            padding: 10px;
          }

          input {
            margin: auto auto;
            width: 200px;
          }

          .description {
            font-family: "Arial";
            font-size: "10px";
          }

          ul {
            padding: 0;
          }

          li {
            list-style: none;
            margin: 5px 0;
          }

          a {
            text-decoration: none;
            color: blue;
          }

          a:hover {
            opacity: 0.6;
          }
        `}</style>
      </Layout>
    );
  }
}

export default Home;
